function proxifyUrl(e){return"proxy.php?url="+encodeURIComponent(e)}function unproxifyUrl(e){return decodeURIComponent(e.split("proxy.php?url=")[1])}var jqAjax=$.ajax;$.ajax=function(e){if(e&&e.url){e.url=proxifyUrl(e.url);var t={url:unproxifyUrl(e.url),complete:e.complete,success:e.success,error:e.error};e.complete=function(e,n){t.complete(e,n)};e.success=function(e,n,r){t.success(e,n,r)};e.error=function(e,n,r){t.error(e,n,r)}}jqAjax(e)};